import React from 'react';
import styles from './Cards.module.css';
import {Grid,CardContent,Typography,Card} from '@material-ui/core';
import CountUp from 'react-countup';

const Cards = ({data: {confirmed,recovered,deaths,lastUpdate}}) => {
    //console.log(props);
    if(!confirmed) {
        return 'Loading';
    }
    return (
        <div className={styles.container}>
            <Grid container spacing = {3} justify="center">
                <Grid item component={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Infected</Typography>
                        {/* <Typography variant="h5" >{confirmed.value}</Typography> */}
                        <Typography variant="h5" >
                        <CountUp
                            start={0}
                            end={confirmed.value}
                            duration={2.5}
                            separator=","
                        />   
                        </Typography>
                        {/* <Typography color="textSecondary" gutterBottom>{lastUpdate}</Typography>
                        original format : 2020-05-16T16:32:14.000Z */}
                        <Typography color="textSecondary" gutterBottom>{new Date(lastUpdate).toString()}</Typography>
                        <Typography variant="body2">Number of active cases of Covid-19</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Recovered</Typography>
                        <Typography variant="h5" >Real Data</Typography>
                        <Typography color="textSecondary" gutterBottom>Real Date</Typography>
                        <Typography variant="body2">Number of recoveries from Covid-19</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Deaths</Typography>
                        <Typography variant="h5" >Real Data</Typography>
                        <Typography color="textSecondary" gutterBottom>Real Date</Typography>
                        <Typography variant="body2">Number of deaths caused by Covid-19</Typography>
                    </CardContent>
                </Grid>
            </Grid>
        </div>
    )
}

export default Cards;
